import React,{useState} from 'react';

import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import './ExpenseFilter';
import Card from "../UI/Card";
import ExpenseFilter from './ExpenseFilter';

const Expenses=(props)=> {
  const [filteredYear,setFilteredYear]=useState('2021');

  const changeFilterhandler=(selectedYear)=>{
    setFilteredYear(selectedYear);
  }
  const filteredExpenses=props.items.filter((expense)=>{
    return expense.date.getFullYear().toString() === filteredYear;
  });

  let expenseContent=<p>No items Found.</p>
  if(filteredExpenses.length>0){
    expenseContent=filteredExpenses.map((singleexpense)=>
                   <ExpenseItem 
                      key={singleexpense.id} 
                      title={singleexpense.title}
                      amount={singleexpense.amount}
                      date={singleexpense.date}>
                   </ExpenseItem>)
  }

  return (

    <Card className="expenses">
      <ExpenseFilter onChangeFilter={changeFilterhandler} selected={filteredYear} ></ExpenseFilter>


      {expenseContent}
    </Card>
  );
}

export default Expenses;
